<div class="div-for-from"><form action="<?=((isset($id))?'update.php?id='.$id:'create.php')?>" method="POST"><label>Name<br><input type="text" name="name" value="<?=((isset($id))?$result['name']:'')?>"></label><br><br><label>Email<br><input type="text" name="email" value="<?=((isset($id))?$result['email']:'')?>"></label><br><br><label>Phone<br><input type="text" name="phone" value="<?=((isset($id))?$result['phone']:'')?>"></label><br><br><label>Type<br><select name="type_id"><?php foreach($types as $row){echo'<option value="'.$row['id'].'" '.(($row['title']==$result['type'])?'selected':'').'>'.$row['title'].'</option>';}?></select><br><br><label>Date Start<br><input id="datepicker" type="text" name="date_start" value="<?php if(isset($id)){echo $result['date_start'];}else{echo '';}?>"></label><br><br><label>Count Days<br><input type="text" name="count_days" value="<?=((isset($id))?$result['count_days']:'')?>"></label><br><br><label>Data<br><input type="text" name="data" value="<?=((isset($id))?$result['data']:'')?>"></label><input type="hidden" name="formSent"><br><br><button type="submit" class="create-new-element"><?=(isset($id)?'Update':'Create')?></button></form></div>